			public static void VerifyHomePhoneMoveMyService(final ITestContext testContext)throws Exception
			{
				
				
			 {
				try
				{
					WebDriver lDriver = UI.getDriver(testContext.getName());  
					OR_AccountOverview oR_AccountOverview = PageFactory.initElements(lDriver, OR_AccountOverview.class);
					OR_HomePhoneService oR_HomePhoneService = PageFactory.initElements(lDriver, OR_HomePhoneService.class);
					
					//boolean result;
					if(UI.SelectServiceFromSecondaryNavigation(oR_AccountOverview.lnkHomePhoneSecondaryNav ,null))
					{
					
						//navigate to home phone service page
						if(UI.WaitForObject(oR_HomePhoneService.elmMyHomePhoneService, 120))
						{
							
							
							Report.TestPoint(testContext.getName(), "Goto Homephone from global nav", "Home Phone Service page is displayed", "Home Phone Service page is displayed" , true);
					
							if(UI.WaitForObject(oR_HomePhoneService.lnkMoveMyService,50))
							{
								oR_HomePhoneService.lnkMoveMyService.click();
								
								Report.TestPoint(testContext.getName(), "Verify link Move My Service is present", "link Move My Service is present and clicked", "link Move My Service is present and clicked" , true);
								
								String winHandleBefore = lDriver.getWindowHandle();

							    // Switch to new window opened

							    for (String winHandle : lDriver.getWindowHandles()) {
							        lDriver.switchTo().window(winHandle);
							    }
							    //check current window url
							    String url=lDriver.getCurrentUrl();
							    String actualurl="https://www.att.com/shop/movers/u-verse.html";
							    		
								if(url.contains(actualurl) || url.contains("https://www.att.com/shop"))
								{
									Report.ValidationPoint(testContext.getName(), "Verify navigated to required url. Requied url = "+actualurl+" | Actual(Current) URL = "+url, "True", "True", true);	
								}
								else
								{
									Report.ValidationPoint(testContext.getName(), "Verify navigated to required url. Requied url = "+actualurl+" | Actual(Current) URL = "+url, "True", "False", true);
								}
								//back to home phones page
								
								lDriver.switchTo().window(winHandleBefore);
								lDriver.navigate().back();
								Thread.sleep(5000);
							}
							else
							{
								Report.TestPoint(testContext.getName(), "Verify link Move My Service is present", "link Move My Service is  present", "link Move My Service is NOT present" , true);
							}
					
					
							UI.SelectServiceFromSecondaryNavigation(oR_HomePhoneService.lnkOverviewSecondaryNav ,null);
							
						}
						else
						{
							Report.TestPoint(testContext.getName(), "Goto Homephone from global nav", "Home Phone Service page is displayed", "Home Phone Service page is displayed" , true);
						}
						
					}
					
					
				}
				catch (Exception e) 
				{
					Report.TestPoint(testContext.getName(),
							"Some error has occured", "True",
							e.getMessage(), true);
				
					
				}
				
			 }
			
			}		 
			
			
