	public static void VerifyMyEquipmentsSection(final ITestContext testContext) throws HeadlessException, IOException, AWTException
	{
		try
		{
			WebDriver lDriver = UI.getDriver(testContext.getName());
			OR_AccountOverview oR_AccountOverview = PageFactory.initElements(lDriver, OR_AccountOverview.class);
			OR_InternetService oR_InternetService = PageFactory.initElements(lDriver, OR_InternetService.class);
			Hashtable<String, String> sTestParams = IO.getTestData(testContext.getName());

			//navigate to internet page
			UI.SelectServiceFromSecondaryNavigation(oR_AccountOverview.lnkInternetSecondaryNav, null, lDriver);
			
			boolean bInternetHome = UI.WaitForObject(oR_InternetService.txtMyInternetService, UI.iObjTimeOut, lDriver);
			Report.ValidationPoint(testContext.getName(), "Verify that internet landing page is diplayed", "true",String.valueOf(bInternetHome), true);
			
			//Step 3. Verify the positioning of the My Equipment section will be displayed adjacent to the 'Plan details' section 
			int iY_Equipment = oR_InternetService.txtHrMyEquipment_1511.getLocation().getY();
			int iY_PlanDetails = oR_InternetService.txtHrPlanDetails_1511.getLocation().getY();
			if(iY_Equipment==iY_PlanDetails)
			{
				Report.ValidationPoint(testContext.getName(), "Verify that the positioning of the My Equipment section will be displayed adjacent to the 'Plan details' section", "true","true", true);
			}else
			{
				Report.ValidationPoint(testContext.getName(), "Verify that the positioning of the My Equipment section will be displayed adjacent to the 'Plan details' section", "true","false", true);
			}
			
			//Step 4. The static text Residential Gateway Router (check the final content in CRD) would display under the My Equipment header
			int iY_RouterPosition = oR_InternetService.txtResidentialGatewayRouter.getLocation().getY();
			int iY_EquipmentPos = oR_InternetService.txtHrMyEquipment_1511.getLocation().getY();
			if(iY_RouterPosition>iY_EquipmentPos)
			{
				Report.ValidationPoint(testContext.getName(), "Verify that static text Residential Gateway Router would display under the My Equipment header", "true","true", true);
			}else
			{
				Report.ValidationPoint(testContext.getName(), "Verify that static text Residential Gateway Router would display under the My Equipment header", "true","false", true);
			}
			
			//Step 5. The My Equipment section should contains CTAs under "Help & Support" field. 
			List <WebElement> CTAs = lDriver.findElements(By.xpath("//p/*[contains(text(),'Help & support')]//parent::p//parent::div//a"));
			if(CTAs.size() < 1 | CTAs!=null)
			{
				Report.ValidationPoint(testContext.getName(), "Verify that My Equipment section should contains CTAs under 'Help & Support' field", "true","true", true);
				
			}else
			{
				Report.ValidationPoint(testContext.getName(), "Verify that My Equipment section should contains CTAs under 'Help & Support' field", "true","false", true);
			}
			
			//Step 6. A CTA "View your Wi-Fi name and password" should display under My Equipment Section on Internet details page
			boolean bViewWifi = UI.WaitForObject(oR_InternetService.lnkViewMyWifiNameAndPassword, UI.iObjTimeOut, lDriver);
			Report.ValidationPoint(testContext.getName(), "Verify that a CTA 'View your Wi-Fi name and password' should display under My Equipment Section on Internet details page", "true",String.valueOf(bViewWifi), true);
			
			String sPreviousURL = lDriver.getCurrentUrl();
			//Step 7. On clicking CTA "View your Wi-Fi name and password" user should be redirected to "https://ufix.att.com?referrerId=1&go=managewifi" in authenticated state in the same browser window
			oR_InternetService.lnkViewMyWifiNameAndPassword.click();
			Report.OperationPoint(testContext.getName(), "Operational - Clicked on 'View your Wi-Fi name and password' link");
			Thread.sleep(8000);
			lDriver.manage().timeouts().implicitlyWait(10, TimeUnit.SECONDS);
			String sNewURL = lDriver.getCurrentUrl();
			if(sNewURL.contains("//ufix.att.com") && sNewURL.contains("managewifi"))
			{
				Report.ValidationPoint(testContext.getName(), "Verify that On clicking CTA 'View your Wi-Fi name and password' user should be redirected to 'https://ufix.att.com?referrerId=1&go=managewifi' in authenticated state in the same browser window, New URL = "+sNewURL, "true","true", true);
			}else
			{
				Report.ValidationPoint(testContext.getName(), "Verify that On clicking CTA 'View your Wi-Fi name and password' user should be redirected to 'https://ufix.att.com?referrerId=1&go=managewifi' in authenticated state in the same browser window, New URL = "+sNewURL, "true","false", true);
			}
			//navigate back
			lDriver.get(sPreviousURL);
			//lDriver.navigate().back();
			lDriver.manage().timeouts().implicitlyWait(10, TimeUnit.SECONDS);
			Report.OperationPoint(testContext.getName(), "Navigating back to Internet landing page");
			
			//Step 8. The CTA "Troubleshoot your network" should navigate the customer to att.com \ ufix in the same browser window.
			boolean bViewTrobleshoot = UI.WaitForObject(oR_InternetService.lnkTroubleshootNetwork, UI.iObjTimeOut, lDriver);
			Report.ValidationPoint(testContext.getName(), "Verify that a CTA 'Troubleshoot your network' should display under My Equipment Section on Internet details page", "true",String.valueOf(bViewTrobleshoot), true);
			oR_InternetService.lnkTroubleshootNetwork.click();
			Report.OperationPoint(testContext.getName(), "Operational - Clicked on 'Troubleshoot your network' link");
			Thread.sleep(8000);
			String sNewURL2 = lDriver.getCurrentUrl();
			if(sNewURL2.contains("//ufix.att.com") | sNewURL2.contains("att.com/ufix") | sNewURL2.contains("/ufix"))
			{
				Report.ValidationPoint(testContext.getName(), "Verify that On clicking CTA 'Troubleshoot your network' should navigate the customer to att.com/ufix in the same browser window, New URL = "+sNewURL2, "true","true", true);
			}else
			{
				Report.ValidationPoint(testContext.getName(), "Verify that On clicking CTA 'Troubleshoot your network' should navigate the customer to att.com/ufix in the same browser window, New URL = "+sNewURL2, "true","false", true);
			}
			//navigate back
			lDriver.get(sPreviousURL);
			//lDriver.navigate().back();
			lDriver.manage().timeouts().implicitlyWait(10, TimeUnit.SECONDS);
			Report.OperationPoint(testContext.getName(), "Navigating back to Internet landing page");
		}
		catch (Exception e) 
		{
			e.printStackTrace();
			Report.TestPoint(testContext.getName(), "true", "true", "Failed to validate"+e.getMessage(), true);

		}
		
	}
	
