	public static void VerifyHomePhoneMRCsection(final ITestContext testContext) throws HeadlessException, IOException, AWTException
	{
		try
		{
			WebDriver lDriver = UI.getDriver(testContext.getName());
			OR_AccountOverview oR_AccountOverview = PageFactory.initElements(lDriver, OR_AccountOverview.class);
			OR_HomePhoneService oR_HomePhoneService = PageFactory.initElements(lDriver, OR_HomePhoneService.class);
			Hashtable<String, String> sTestParams = IO.getTestData(testContext.getName());
		
			//Navigate to Homephone page
			UI.SelectServiceFromSecondaryNavigation(oR_AccountOverview.lnkHomePhoneSecondaryNav, null, lDriver);
			Report.OperationPoint(testContext.getName(), "Operational - Navigating to HomePhone landing page");
			boolean bHomePhoneHome = UI.WaitForObject(oR_HomePhoneService.elmHomePhoneService, UI.iObjTimeOut, lDriver);
			Report.ValidationPoint(testContext.getName(), "Verify that Homephone landing page is diplayed", "true",String.valueOf(bHomePhoneHome), true);
			
			//Verify the Plan Information section on HomePhone landing page
			boolean bMonthlyCostText = UI.WaitForObject(oR_HomePhoneService.txtHrTotalMonthlyVoiceCost_1511, UI.iObjTimeOut, lDriver);
			Report.ValidationPoint(testContext.getName(), "Verify that plan information section is diplayed", "true",String.valueOf(bMonthlyCostText), true);
			//Total monthly cost as static content should be displayed under MTC section on HomePhone landing page as per HTML/ Comps
			boolean bMonthlyCost = UI.WaitForObject(oR_HomePhoneService.txtTotalMonthlyVoiceCost_1511, UI.iObjTimeOut, lDriver);
			Report.ValidationPoint(testContext.getName(), "Verify that Total monthly cost as static content should be displayed under MTC section on HomePhone landing page", "true",String.valueOf(bMonthlyCost), true);
			//MRC value should be formatted as $##.## with an * (asterisk) for a message 
			String sMontlyCost = oR_HomePhoneService.txtTotalMonthlyVoiceCost_1511.getText();
			String sPatt = "\\$\\d\\d.*\\.\\d\\d.*\\*";
			if(sMontlyCost.matches(sPatt))
			{
				Report.ValidationPoint(testContext.getName(), "Verify that MRC value should be formatted as $##.## with an * (asterisk) for a message", "true","true", true);
			}else
			{
				Report.ValidationPoint(testContext.getName(), "Verify that MRC value should be formatted as $##.## with an * (asterisk) for a message", "true","false", true);
			}
			//The MRC value should reflect the charges for uVerse HomePhone service, sent by Billing.
			
			//Veirfy the static message shown with an * (asterisk) under MRC section
			boolean bStaticText = UI.WaitForObject(oR_HomePhoneService.txtStaticTextUnderMRC, UI.iObjTimeOut, lDriver);
			Report.ValidationPoint(testContext.getName(), "Verify that a static message shown with an * (asterisk) under MRC section. Message displayed is : "+oR_HomePhoneService.txtStaticTextUnderMRC, "true",String.valueOf(bStaticText), true);

			//Verify the presence of CTA View Cost Summary under MRC section
			boolean bViewCostSum = UI.WaitForObject(oR_HomePhoneService.lnkViewCostSummary_1511, UI.iObjTimeOut, lDriver);
			Report.ValidationPoint(testContext.getName(), "Verify the presence of CTA View Cost Summary under MRC section", "true",String.valueOf(bViewCostSum), true);
			//On clicking the CTA View Cost Summary, MRC flyout modal should open
			oR_HomePhoneService.lnkViewCostSummary_1511.click();
			Report.OperationPoint(testContext.getName(), "Operational - Clicked on CTA View Cost Summary");
			
			//MRC flyout modal should display the line item content in the sort order and groups as received from Billing
			//Charges and Total should be in $XX.XX/mo.format
				lDriver.manage().timeouts().implicitlyWait(10, TimeUnit.SECONDS);
				boolean bPopup = UI.WaitForObject(oR_HomePhoneService.elmViewCostSummaryPopup, UI.iObjTimeOut, lDriver);
				Report.ValidationPoint(testContext.getName(), "Verify that on clicking the CTA View Cost Summary, MRC flyout modal should open", "true",String.valueOf(bPopup), true);
				String sMontlyCostInPopup = oR_HomePhoneService.txtTotalMonthlyCostAmountUnderPopup.getText();
				if(sMontlyCostInPopup.matches(sPatt))
				{
					Report.ValidationPoint(testContext.getName(), "Verify that charges and Total should be in $XX.XX/mo.format", "true","true", true);
				}else
				{
					Report.ValidationPoint(testContext.getName(), "Verify that charges and Total should be in $XX.XX/mo.format", "true","false", true);
				}
				//Static content should display with message and * (asterisk) (see CRD for final content) on MRC flyout modal
				boolean bStaticMsg = UI.WaitForObject(oR_HomePhoneService.txtStaticTextUnderMRCPopup, UI.iObjTimeOut, lDriver);
				Report.ValidationPoint(testContext.getName(), "Verify that a static message shown with an * (asterisk) under MRC flyout modal", "true",String.valueOf(bStaticMsg), true);

			//Verify the behaviour of the MRC flyout modal when X button is clicked on the modal or user clicks outside the modal area
			oR_HomePhoneService.btnCloseSummaryPopup.click();
			Report.OperationPoint(testContext.getName(), "Operational - Clicked on close button for Cost summary popup");
			lDriver.manage().timeouts().implicitlyWait(10, TimeUnit.SECONDS);
			try{	
					boolean bPopupCheck = UI.WaitForObject(oR_HomePhoneService.elmViewCostSummaryPopup, UI.iObjTimeOut, lDriver);
					if(bPopupCheck==true)
					{
						Report.ValidationPoint(testContext.getName(), "Verify that when X button is clicked on the modal or user clicks outside the modal area, MRC flyout modal should be closed", "true","false", true);

					}else
					{
						Report.ValidationPoint(testContext.getName(), "Verify that when X button is clicked on the modal or user clicks outside the modal area, MRC flyout modal should be closed", "true","true", true);
					}
			}catch(Exception Ee2)
			{
				Report.ValidationPoint(testContext.getName(), "Verify that when X button is clicked on the modal or user clicks outside the modal area, MRC flyout modal should be closed", "true","true", true);
			}
		}
		catch (Exception e) 
		{
			e.printStackTrace();
			Report.TestPoint(testContext.getName(), "true", "true", "Failed to validate"+e.getMessage(), true);

		}
		
	}
	
