	public static void NextGenTVProgramming(final ITestContext testContext) throws HeadlessException, IOException, AWTException
	{
		try
		{
			
			WebDriver lDriver = UI.getDriver(testContext.getName());   
			
			OR_AccountOverview oR_AccountOverview = PageFactory.initElements(lDriver, OR_AccountOverview.class);
			OR_TelevisionService oR_TelevisionService = PageFactory.initElements(lDriver, OR_TelevisionService.class);
			
			//Navigating to Digital TV page
			Report.OperationPoint(testContext.getName(), "Navigating to Digital TV page");
			UI.SelectServiceFromSecondaryNavigation(oR_AccountOverview.lnkDigitalTVSecondaryNav, null);
			
			// Validate user is navigated to My Tv Service Page
			Boolean bMyTVService = UI.WaitForObject(oR_TelevisionService.txtMyTVService, UI.iObjTimeOut, lDriver);
			if(bMyTVService){
				Report.OperationPoint(testContext.getName(), "Validate User is navigated to My TV Service Page ");
			}
			
			// Validate the new My Programming section is seen
			Boolean bProgramming = UI.WaitForObject(oR_TelevisionService.txtMyProgramming, UI.iObjTimeOut, lDriver);
			if(bProgramming){
				Report.ValidationPoint(testContext.getName(), "Validate My Programming section is seen.", "True", "True", true);
			}
			
			// Validate that customer will see  CTAs at the bottom of the My Programming section
			Boolean bMyProgrammingcontent = UI.WaitForObject(oR_TelevisionService.lnkMyProgrammingcontent, UI.iObjTimeOut,lDriver);
			if(bMyProgrammingcontent){
				Report.ValidationPoint(testContext.getName(), "Validate that customer will see CTAs at the bottom of the My Programming section.", "True", "True", true);
				List<WebElement> CTA = lDriver.findElements(By.xpath("//*[contains(text(),'My programming')]//parent::div//a"));
				System.out.println(CTA.size());
				String [] strCTA = new String[CTA.size()];
				
				for (int i=0; i<CTA.size(); i++){
					strCTA[i]=CTA.get(i).getText();
				}
				
				//Retrieving the link headings
				for(int j=0;j<CTA.size();j++)
				{
					if(strCTA[j].length()>1)
					{
						
						Report.OperationPoint(testContext.getName()," Link "+strCTA[j] + " is present");
					}
				}
				
			}
			
			// Validate that a CTA View Channel Lineup will be displayed on the left
			Boolean bViewChanneLineup = UI.WaitForObject(oR_TelevisionService.lnkViewChannel, UI.iObjTimeOut, lDriver);
			if(bViewChanneLineup){
				Report.ValidationPoint(testContext.getName(), "Validate Viewchannellineup link is present.", "True", "True", true);
			}
			
			String parentHandle = lDriver.getWindowHandle(); 	// get the current window handle
			  
			// Clicking on Viewchannellineup link
			
			Report.ValidationPoint(testContext.getName(), "Clicking on Viewchannellineup link.", "True", "True", true);
			oR_TelevisionService.lnkViewChannel.click();
			Thread.sleep(5000);
			
			 for (String winHandle : lDriver.getWindowHandles()) {
			      lDriver.switchTo().window(winHandle);		// switch focus of WeblDriver to the next found window handle 
			      Thread.sleep(5000);
			 }
			  
			// Validate that user is navigated on to 'AT&T U-verse Channel Lineup' page
			 
			Boolean blineuppage = UI.WaitForObject(oR_TelevisionService.txtAtTChannelLineup, UI.iObjTimeOut, lDriver);
			if(blineuppage){
				Report.TestPoint(testContext.getName(), "Verify that user is navigated on to 'AT&T U-verse Channel' Lineup page in a new window.", "true", "true", true);
				
			}
			lDriver.close();               // close newly opened window when done with it
			lDriver.switchTo().window(parentHandle); 
			Thread.sleep(5000);
			
			// Validate that ManageDVR link is present
			
			Boolean bManageDVR = UI.WaitForObject(oR_TelevisionService.lnkManageDVR, UI.iObjTimeOut,lDriver);
			if(bManageDVR){
				Report.ValidationPoint(testContext.getName(), "Validate ManageDVR link is present.", "True", "True", true);
				
				// Clicking on MaangeDVR link
				
				oR_TelevisionService.lnkManageDVR.click();
				Thread.sleep(5000);
				
				for (String winHandle : lDriver.getWindowHandles()) {
				      lDriver.switchTo().window(winHandle); 	// switch focus of WeblDriver to the next found window handle 
				 }
				
				// Validate that user is navigated on to 'AT&T Log in' page
				
				Boolean blogin = UI.WaitForObject(oR_TelevisionService.txtManageDVRLogin, UI.iObjTimeOut, lDriver);
				if(blogin){
					Report.TestPoint(testContext.getName(), "Verify that user is navigated on to 'AT&T Login' page in a new window.", "true", "true", true);
					
				}
				lDriver.close();              // close newly opened window when done with it
				lDriver.switchTo().window(parentHandle); 
				Thread.sleep(5000);
			}
		}
		
		catch (Exception e) 
		{
			e.printStackTrace();
			Report.TestPoint(testContext.getName(), "some error occured", "true", "Failed to validate"+e.getMessage(), true);

		}
		
	  }
