	//SER16506
	public static void ValidateWirelessAlertCTAChangeForPlanChange(final ITestContext testContext) throws HeadlessException, IOException, AWTException{
	
		WebDriver lDriver = UI.getDriver(testContext.getName()); 
		OR_AccountOverview oR_AccountOverview = PageFactory.initElements(lDriver, OR_AccountOverview.class);
	
		try
		{
			//Validating Alerts on Overview Page
			Boolean bAlert = UI.WaitForObject(oR_AccountOverview.btnAlert, UI.iObjTimeOut, lDriver);
			if(bAlert){
				Report.ValidationPoint(testContext.getName(),"Validating Alerts present on Overview Page.","true","true",true);
			} 
			
			// Click
			Report.ValidationPoint(testContext.getName(),"Clicking, to view details regarding Alerts.","true","true",true);
			oR_AccountOverview.btnAlert.click();
		

			// Getting alert count
			
			Boolean bAlertCount = UI.WaitForObject(oR_AccountOverview.txtAlertButtonCount, UI.iObjTimeOut, lDriver);
			if(bAlertCount){
				Report.ValidationPoint(testContext.getName(),"Total number of alerts present are : " + oR_AccountOverview.txtAlertButtonCount.getText(),"true","true",true);
				String stralertcount = oR_AccountOverview.txtAlertButtonCount.getText();
				int count = Integer.valueOf(stralertcount);
				if(count > 5){
					Boolean bScrollDown = UI.WaitForObject(oR_AccountOverview.btnDownarrow, UI.iObjTimeOut, lDriver);
					if(bScrollDown){
						Report.ValidationPoint(testContext.getName(),"Plan alert is not seen in this list, hence scrolling down.","true","true",true);
						oR_AccountOverview.btnDownarrow.click();
					}
					
				}
			}
			
			
			Boolean bPlanChangeAlert= UI.WaitForObject(oR_AccountOverview.txtPlanChangeAlert, UI.iObjTimeOut, lDriver);
			if(bPlanChangeAlert){
				Report.ValidationPoint(testContext.getName(),"Plan Change Alert is present.","true","true",true);
				
				// View Details validations
				
				Boolean bViewDetailsUnderPlanChangeAlert = UI.WaitForObject(oR_AccountOverview.lnkViewDetailsUnderPlanChangeAlert, UI.iObjTimeOut, lDriver);
				if(bViewDetailsUnderPlanChangeAlert){
					Report.ValidationPoint(testContext.getName(),"Clicking on View Details link for Plan Change Alert.","true","true",true);
					oR_AccountOverview.lnkViewDetailsUnderPlanChangeAlert.click();
					
					Boolean bMyWirelessService = UI.WaitForObject(oR_AccountOverview.txtMyWirelessService, UI.iObjTimeOut, lDriver);
					if(bMyWirelessService){
						String sURLNew = lDriver.getCurrentUrl();
						if(sURLNew.contains("displayPlansFeaturesHub")){
							Report.ValidationPoint(testContext.getName(),"URL ======"+ sURLNew+" is displayed, which is correct URL.","true","true",true);
						} else {
							Report.ValidationPoint(testContext.getName(),"URL ======"+ sURLNew+" is displayed, which is correct URL.","true","false",true);
						}
						
						lDriver.navigate().back();
						
					}	
				} else {
					Report.ValidationPoint(testContext.getName(),"Some error occurred while clicking on 'View Details' for Plan change alert.","true","true",true);
				}
			} 
		}
		catch(Exception e)
		{
			Report.TestPoint(testContext.getName(),"Some error has occured", "True",e.getMessage(), true);
		}
	}
